import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SwapPilot Fee Collector (DEPRECATED)
 * @notice Collects platform fees and distributes according to tokenomics:
 *         - 15% -> Buy and burn PILOT
 *         - 80% -> Treasury
 *         - 5% -> Referral pool
 *
 * @custom:deprecated This contract is deprecated. Use FeeCollectorV2 instead.
 *   V1 has known security issues (zero slippage protection, public distributeFees,
 *   burn to dead address instead of _burn). DO NOT deploy to production.
 *   Kept for historical reference and existing test compatibility only.
 */
contract FeeCollector is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /// @notice Distribution percentages (must sum to 100)
    uint256 public constant BURN_PERCENT = 15;
    uint256 public constant TREASURY_PERCENT = 80;
    uint256 public constant REFERRAL_PERCENT = 5;

    /// @notice PILOT token address
    IERC20 public immutable pilotToken;

    /// @notice Treasury wallet
    address public treasury;

    /// @notice Referral pool wallet
    address public referralPool;

    /// @notice DEX router for buying PILOT (e.g., PancakeSwap)
    address public dexRouter;

    /// @notice WBNB address for swap path
    address public wbnb;

    /// @notice Total fees collected (in BNB wei)
    uint256 public totalFeesCollected;

    /// @notice Total PILOT burned
    uint256 public totalPilotBurned;

    /// @notice Emitted when fees are collected
    event FeesCollected(address indexed token, uint256 amount);

    /// @notice Emitted when fees are distributed
    event FeesDistributed(
        uint256 burnAmount,
        uint256 treasuryAmount,
        uint256 referralAmount
    );

    /// @notice Emitted when PILOT is bought and burned
    event PilotBurned(uint256 bnbSpent, uint256 pilotBurned);

    /// @notice Emitted on emergency withdrawal
    event EmergencyWithdraw(address indexed token, uint256 amount, address indexed to);

    constructor(
        address _pilotToken,
        address _treasury,
        address _referralPool,
        address _dexRouter,
        address _wbnb
    ) Ownable(msg.sender) {
        require(_pilotToken != address(0), "Invalid PILOT address");
        require(_treasury != address(0), "Invalid treasury address");
        require(_referralPool != address(0), "Invalid referral pool address");

        pilotToken = IERC20(_pilotToken);
        treasury = _treasury;
        referralPool = _referralPool;
        dexRouter = _dexRouter;
        wbnb = _wbnb;
    }

    /**
     * @notice Receive BNB fees
     */
    receive() external payable {
        totalFeesCollected += msg.value;
        emit FeesCollected(address(0), msg.value);
    }

    /**
     * @notice Distribute collected BNB fees according to tokenomics
     */
    function distributeFees() external nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to distribute");

        // Calculate distribution
        uint256 burnAmount = (balance * BURN_PERCENT) / 100;
        uint256 treasuryAmount = (balance * TREASURY_PERCENT) / 100;
        uint256 referralAmount = balance - burnAmount - treasuryAmount;

        // Send to treasury
        (bool treasurySuccess, ) = treasury.call{value: treasuryAmount}("");
        require(treasurySuccess, "Treasury transfer failed");

        // Send to referral pool
        (bool referralSuccess, ) = referralPool.call{value: referralAmount}("");
        require(referralSuccess, "Referral transfer failed");

        // Buy and burn PILOT with remaining
        if (burnAmount > 0 && dexRouter != address(0)) {
            _buyAndBurnPilot(burnAmount);
        }

        emit FeesDistributed(burnAmount, treasuryAmount, referralAmount);
    }

    /**
     * @notice Buy PILOT on DEX and burn it
     * @param bnbAmount Amount of BNB to spend
     */
    function _buyAndBurnPilot(uint256 bnbAmount) internal {
        // Build swap path: BNB -> PILOT
        address[] memory path = new address[](2);
        path[0] = wbnb;
        path[1] = address(pilotToken);

        // Get router interface
        IPancakeRouter router = IPancakeRouter(dexRouter);

        // Swap BNB for PILOT
        uint256 pilotBefore = pilotToken.balanceOf(address(this));

        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: bnbAmount}(
            0, // Accept any amount
            path,
            address(this),
            block.timestamp + 300
        );

        uint256 pilotReceived = pilotToken.balanceOf(address(this)) - pilotBefore;

        // Burn the PILOT
        if (pilotReceived > 0) {
            // Transfer to burn address
            pilotToken.safeTransfer(0x000000000000000000000000000000000000dEaD, pilotReceived);
            totalPilotBurned += pilotReceived;
            emit PilotBurned(bnbAmount, pilotReceived);
        }
    }

    /**
     * @notice Update treasury address
     */
    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid address");
        treasury = _treasury;
    }

    /**
     * @notice Update referral pool address
     */
    function setReferralPool(address _referralPool) external onlyOwner {
        require(_referralPool != address(0), "Invalid address");
        referralPool = _referralPool;
    }

    /**
     * @notice Update DEX router
     */
    function setDexRouter(address _dexRouter) external onlyOwner {
        dexRouter = _dexRouter;
    }

    /**
     * @notice Emergency withdraw (owner only)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(token).safeTransfer(msg.sender, amount);
        }
        emit EmergencyWithdraw(token, amount, msg.sender);
    }

    /**
     * @notice Convert ERC-20 token fees to BNB
     * @param token The token to convert
     * @param minBnbOut Minimum BNB to receive (slippage protection)
     */
    function convertTokenToBnb(address token, uint256 minBnbOut) external nonReentrant {
        require(token != address(0), "Use BNB directly");
        require(token != address(pilotToken), "Cannot convert PILOT");
        require(dexRouter != address(0), "DEX router not set");

        uint256 tokenBalance = IERC20(token).balanceOf(address(this));
        require(tokenBalance > 0, "No tokens to convert");

        // Approve router
        IERC20(token).safeIncreaseAllowance(dexRouter, tokenBalance);

        // Build swap path: Token -> WBNB
        address[] memory path = new address[](2);
        path[0] = token;
        path[1] = wbnb;

        // Swap tokens for BNB
        IPancakeRouter(dexRouter).swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenBalance,
            minBnbOut,
            path,
            address(this),
            block.timestamp + 300
        );

        emit FeesCollected(token, tokenBalance);
    }

    /**
     * @notice Collect ERC-20 token fees (tokens sent directly to this contract)
     * @param token The token that was sent
     */
    function collectTokenFees(address token) external {
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            emit FeesCollected(token, balance);
        }
    }
}

/**
 * @notice Minimal PancakeSwap Router interface
 */
interface IPancakeRouter {
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

src/FeeCollectorV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SwapPilot Fee Collector V2 (DappBay Compliant)
 * @notice Collects platform fees and distributes according to tokenomics
 * 
 * @dev Changes from V1 for DappBay compliance:
 * - Added Pausable for circuit breaker
 * - Added events for all admin parameter changes
 * - Added slippage protection with oracle support
 * - Restricted distributeFees() to onlyOwner
 * - Fixed burn mechanism to use actual burn() instead of dead address
 * - Added minimum distribution threshold
 * - Note: Deploy with TimelockController as owner for emergency withdrawal protection
 */
contract FeeCollectorV2 is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    /// @notice Distribution percentages (must sum to 100)
    uint256 public constant BURN_PERCENT = 15;
    uint256 public constant TREASURY_PERCENT = 80;
    uint256 public constant REFERRAL_PERCENT = 5;

    /// @notice PILOT token address
    IERC20 public immutable pilotToken;

    /// @notice Treasury wallet
    address public treasury;

    /// @notice Referral pool wallet
    address public referralPool;

    /// @notice DEX router for buying PILOT (e.g., PancakeSwap)
    address public dexRouter;

    /// @notice WBNB address for swap path
    address public wbnb;

    /// @notice Minimum BNB balance to trigger distribution (prevents dust gas waste)
    uint256 public minDistributionAmount = 0.1 ether;

    /// @notice Total fees collected (in BNB wei)
    uint256 public totalFeesCollected;

    /// @notice Total PILOT burned (actual supply reduction)
    uint256 public totalPilotBurned;

    /// @notice Emitted when fees are collected
    event FeesCollected(address indexed token, uint256 amount);

    /// @notice Emitted when fees are distributed
    event FeesDistributed(
        uint256 burnAmount,
        uint256 treasuryAmount,
        uint256 referralAmount
    );

    /// @notice Emitted when PILOT is bought and burned
    event PilotBurned(uint256 bnbSpent, uint256 pilotBurned);

    /// @notice Emitted when treasury address is updated
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);

    /// @notice Emitted when referral pool address is updated
    event ReferralPoolUpdated(address indexed oldPool, address indexed newPool);

    /// @notice Emitted when DEX router is updated
    event DexRouterUpdated(address indexed oldRouter, address indexed newRouter);

    /// @notice Emitted when minimum distribution amount is updated
    event MinDistributionAmountUpdated(uint256 oldAmount, uint256 newAmount);

    /// @notice Emitted on emergency withdrawal
    event EmergencyWithdraw(address indexed token, uint256 amount, address indexed to);

    constructor(
        address _pilotToken,
        address _treasury,
        address _referralPool,
        address _dexRouter,
        address _wbnb
    ) Ownable(msg.sender) {
        require(_pilotToken != address(0), "Invalid PILOT address");
        require(_treasury != address(0), "Invalid treasury address");
        require(_referralPool != address(0), "Invalid referral pool address");

        pilotToken = IERC20(_pilotToken);
        treasury = _treasury;
        referralPool = _referralPool;
        dexRouter = _dexRouter;
        wbnb = _wbnb;
    }

    /**
     * @notice Pause the contract (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Receive BNB fees
     */
    receive() external payable whenNotPaused {
        totalFeesCollected += msg.value;
        emit FeesCollected(address(0), msg.value);
    }

    /**
     * @notice Distribute collected BNB fees according to tokenomics
     * @param minPilotOut Minimum PILOT tokens to receive (slippage protection)
     * 
     * @dev Restricted to owner (or keeper) to prevent MEV timing exploitation
     */
    function distributeFees(uint256 minPilotOut) external onlyOwner nonReentrant whenNotPaused {
        uint256 balance = address(this).balance;
        require(balance >= minDistributionAmount, "Below minimum distribution threshold");

        uint256 burnAmount = (balance * BURN_PERCENT) / 100;
        uint256 treasuryAmount = (balance * TREASURY_PERCENT) / 100;
        // Calculate referral as remainder to avoid rounding dust accumulation
        uint256 referralAmount = balance - burnAmount - treasuryAmount;

        if (burnAmount > 0 && dexRouter != address(0) && wbnb != address(0)) {
            _buyAndBurnPilot(burnAmount, minPilotOut);
        }

        if (treasuryAmount > 0) {
            (bool success, ) = treasury.call{value: treasuryAmount}("");
            require(success, "Treasury transfer failed");
        }

        if (referralAmount > 0) {
            (bool success, ) = referralPool.call{value: referralAmount}("");
            require(success, "Referral transfer failed");
        }

        emit FeesDistributed(burnAmount, treasuryAmount, referralAmount);
    }

    /**
     * @notice Buy PILOT with BNB and burn it (reduces totalSupply)
     * @param bnbAmount Amount of BNB to spend
     * @param minPilotOut Minimum PILOT to receive (slippage protection)
     */
    function _buyAndBurnPilot(uint256 bnbAmount, uint256 minPilotOut) private {
        address[] memory path = new address[](2);
        path[0] = wbnb;
        path[1] = address(pilotToken);

        // Buy PILOT from DEX
        uint256 pilotBefore = pilotToken.balanceOf(address(this));

        (bool success, ) = dexRouter.call{value: bnbAmount}(
            abi.encodeWithSignature(
                "swapExactETHForTokensSupportingFeeOnTransferTokens(uint256,address[],address,uint256)",
                minPilotOut, // Slippage protection
                path,
                address(this),
                block.timestamp + 300
            )
        );
        require(success, "DEX swap failed");

        // Burn received PILOT (actual supply reduction)
        // Use delta pattern to only burn tokens received from the swap, not pre-existing balance
        uint256 pilotReceived = pilotToken.balanceOf(address(this)) - pilotBefore;
        if (pilotReceived > 0) {
            // Use PILOTToken's burn function to reduce totalSupply
            (bool burnSuccess, ) = address(pilotToken).call(
                abi.encodeWithSignature("feeBurn(uint256)", pilotReceived)
            );
            require(burnSuccess, "Burn failed");

            totalPilotBurned += pilotReceived;
            emit PilotBurned(bnbAmount, pilotReceived);
        }
    }

    /**
     * @notice Update treasury address
     * @param _treasury New treasury address
     */
    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid treasury address");
        address oldTreasury = treasury;
        treasury = _treasury;
        emit TreasuryUpdated(oldTreasury, _treasury);
    }

    /**
     * @notice Update referral pool address
     * @param _referralPool New referral pool address
     */
    function setReferralPool(address _referralPool) external onlyOwner {
        require(_referralPool != address(0), "Invalid referral pool address");
        address oldPool = referralPool;
        referralPool = _referralPool;
        emit ReferralPoolUpdated(oldPool, _referralPool);
    }

    /**
     * @notice Update DEX router address
     * @param _dexRouter New DEX router address
     */
    function setDexRouter(address _dexRouter) external onlyOwner {
        require(_dexRouter != address(0), "Invalid router address");
        address oldRouter = dexRouter;
        dexRouter = _dexRouter;
        emit DexRouterUpdated(oldRouter, _dexRouter);
    }

    /**
     * @notice Update minimum distribution amount
     * @param _minAmount New minimum amount in wei
     */
    function setMinDistributionAmount(uint256 _minAmount) external onlyOwner {
        uint256 oldAmount = minDistributionAmount;
        minDistributionAmount = _minAmount;
        emit MinDistributionAmountUpdated(oldAmount, _minAmount);
    }

    /**
     * @notice Emergency withdrawal (owner only, use with TimelockController)
     * @param token Token address (address(0) for BNB)
     * @param amount Amount to withdraw
     * 
     * @dev Deploy with TimelockController as owner to add delay to this function
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            require(amount <= address(this).balance, "Insufficient balance");
            (bool success, ) = msg.sender.call{value: amount}("");
            require(success, "BNB transfer failed");
        } else {
            IERC20(token).safeTransfer(msg.sender, amount);
        }
        emit EmergencyWithdraw(token, amount, msg.sender);
    }

    /**
     * @notice Convert accumulated ERC-20 tokens to BNB (owner only)
     * @param token Token address to convert
     * @param minBnbOut Minimum BNB to receive (slippage protection)
     */
    function convertTokenToBnb(address token, uint256 minBnbOut) external onlyOwner nonReentrant whenNotPaused {
        require(token != address(0), "Invalid token");
        require(token != address(pilotToken), "Cannot convert PILOT");

        uint256 balance = IERC20(token).balanceOf(address(this));
        require(balance > 0, "No tokens to convert");

        // Approve router
        IERC20(token).safeIncreaseAllowance(dexRouter, balance);

        // Build path
        address[] memory path = new address[](2);
        path[0] = token;
        path[1] = wbnb;

        // Swap to BNB
        (bool success, ) = dexRouter.call(
            abi.encodeWithSignature(
                "swapExactTokensForETHSupportingFeeOnTransferTokens(uint256,uint256,address[],address,uint256)",
                balance,
                minBnbOut,
                path,
                address(this),
                block.timestamp + 300
            )
        );
        require(success, "Token swap failed");
    }
}

src/PILOTToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title PILOT Token
 * @notice Governance and utility token for SwapPilot DEX aggregator
 * 
 * Utility:
 * - Hold PILOT to get fee discounts on swaps (10-20% off)
 * - 15% of platform fees are used to buy and burn PILOT (deflationary)
 * - Future: Governance voting on protocol parameters
 * 
 * Tokenomics:
 * - Max Supply: 1,000,000,000 PILOT (1 billion)
 * - No mint function after initial distribution
 * - Deflationary via burn mechanism
 */
contract PILOTToken is ERC20, ERC20Burnable, Ownable {
    /// @notice Maximum total supply (1 billion tokens with 18 decimals)
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**18;

    /// @notice Whether initial distribution has been completed
    bool public distributionCompleted;

    /// @notice Emitted when tokens are burned via platform fees
    event FeeBurn(address indexed burner, uint256 amount);

    constructor() ERC20("SwapPilot", "PILOT") Ownable(msg.sender) {
        // Initial mint to deployer for distribution
        // Distribution will be done in separate transactions
    }

    /**
     * @notice Mint tokens for initial distribution (only callable once)
     * @param recipients Array of recipient addresses
     * @param amounts Array of amounts to mint to each recipient
     */
    function initialDistribution(
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external onlyOwner {
        require(!distributionCompleted, "Distribution already completed");
        require(recipients.length == amounts.length, "Arrays length mismatch");

        uint256 totalToMint = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalToMint += amounts[i];
        }

        require(totalSupply() + totalToMint <= MAX_SUPPLY, "Exceeds max supply");

        for (uint256 i = 0; i < recipients.length; i++) {
            _mint(recipients[i], amounts[i]);
        }
    }

    /**
     * @notice Complete the initial distribution (locks minting forever)
     */
    function completeDistribution() external onlyOwner {
        distributionCompleted = true;
    }

    /**
     * @notice Burn tokens from platform fee collection
     * @dev Called by the fee collector contract after buying PILOT on market
     * @param amount Amount of PILOT to burn
     */
    function feeBurn(uint256 amount) external {
        _burn(msg.sender, amount);
        emit FeeBurn(msg.sender, amount);
    }

    /**
     * @notice Check if an address qualifies for fee discount
     * @param account Address to check
     * @return tier The discount tier (0 = none, 1 = 10%, 2 = 15%, 3 = 20%)
     * @return discountBps Discount in basis points
     */
    function getDiscountTier(address account) external view returns (uint8 tier, uint16 discountBps) {
        uint256 balance = balanceOf(account);

        if (balance >= 10_000 * 10**18) {
            return (3, 2000); // Gold: 20% discount
        } else if (balance >= 1_000 * 10**18) {
            return (2, 1500); // Silver: 15% discount
        } else if (balance >= 100 * 10**18) {
            return (1, 1000); // Bronze: 10% discount
        }

        return (0, 0); // No discount
    }
}

src/ReferralPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SwapPilot Referral Pool
 * @notice Manages referral rewards distribution and claiming
 * @dev Receives BNB from FeeCollector and allows referrers to claim their share
 */
contract ReferralPool is Ownable, ReentrancyGuard, Pausable {

    /// @notice Pending rewards per referrer (in wei)
    mapping(address => uint256) public pendingRewards;

    /// @notice Total claimed by each referrer
    mapping(address => uint256) public totalClaimed;

    /// @notice Total rewards allocated
    uint256 public totalAllocated;

    /// @notice Total rewards claimed
    uint256 public totalClaimedAmount;

    /// @notice Minimum claim amount (to prevent dust claims, default 0.001 BNB)
    uint256 public minClaimAmount = 0.001 ether;

    /// @notice Referral code to address mapping
    mapping(bytes32 => address) public referralCodes;

    /// @notice Address to referral code mapping (reverse lookup)
    mapping(address => bytes32) public addressToCode;

    /// @notice Emitted when rewards are allocated to a referrer
    event RewardsAllocated(address indexed referrer, uint256 amount);

    /// @notice Emitted when a referrer claims their rewards
    event RewardsClaimed(address indexed referrer, uint256 amount);

    /// @notice Emitted when a referral code is registered
    event ReferralCodeRegistered(address indexed referrer, bytes32 code);

    /// @notice Emitted when BNB is received
    event Received(address indexed from, uint256 amount);

    /// @notice Emitted on emergency withdrawal
    event EmergencyWithdraw(uint256 amount, address indexed to);

    /// @notice Emitted when minimum claim amount is updated
    event MinClaimAmountUpdated(uint256 oldMin, uint256 newMin);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Receive BNB (from FeeCollector or direct deposits)
     */
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    /**
     * @notice Register a referral code for the caller
     * @param code The referral code (keccak256 hash of the code string)
     */
    function registerReferralCode(bytes32 code) external {
        require(code != bytes32(0), "Invalid code");
        require(referralCodes[code] == address(0), "Code already registered");
        require(addressToCode[msg.sender] == bytes32(0), "Already has a code");

        referralCodes[code] = msg.sender;
        addressToCode[msg.sender] = code;

        emit ReferralCodeRegistered(msg.sender, code);
    }

    /**
     * @notice Allocate rewards to a referrer (owner only)
     * @param referrer The referrer's address
     * @param amount The amount of BNB (wei) to allocate
     */
    function allocateRewards(address referrer, uint256 amount) external onlyOwner whenNotPaused {
        require(referrer != address(0), "Invalid referrer");
        require(amount > 0, "Amount must be > 0");
        require(address(this).balance >= totalAllocated - totalClaimedAmount + amount, "Insufficient balance");

        pendingRewards[referrer] += amount;
        totalAllocated += amount;

        emit RewardsAllocated(referrer, amount);
    }

    /**
     * @notice Batch allocate rewards to multiple referrers
     * @param referrers Array of referrer addresses
     * @param amounts Array of amounts to allocate
     */
    function batchAllocateRewards(
        address[] calldata referrers, 
        uint256[] calldata amounts
    ) external onlyOwner whenNotPaused {
        require(referrers.length == amounts.length, "Length mismatch");
        require(referrers.length <= 200, "Too many recipients");

        uint256 totalToAllocate = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalToAllocate += amounts[i];
        }

        require(
            address(this).balance >= totalAllocated - totalClaimedAmount + totalToAllocate, 
            "Insufficient balance"
        );

        for (uint256 i = 0; i < referrers.length; i++) {
            require(referrers[i] != address(0), "Invalid referrer");
            pendingRewards[referrers[i]] += amounts[i];
            totalAllocated += amounts[i];
            emit RewardsAllocated(referrers[i], amounts[i]);
        }
    }

    /**
     * @notice Claim pending rewards
     */
    function claim() external nonReentrant whenNotPaused {
        uint256 amount = pendingRewards[msg.sender];
        require(amount >= minClaimAmount, "Amount below minimum");

        pendingRewards[msg.sender] = 0;
        totalClaimed[msg.sender] += amount;
        totalClaimedAmount += amount;

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");

        emit RewardsClaimed(msg.sender, amount);
    }

    /**
     * @notice Get referrer info
     * @param referrer The referrer's address
     * @return pending Pending rewards
     * @return claimed Total claimed
     * @return code Referral code
     */
    function getReferrerInfo(address referrer) external view returns (
        uint256 pending,
        uint256 claimed,
        bytes32 code
    ) {
        return (
            pendingRewards[referrer],
            totalClaimed[referrer],
            addressToCode[referrer]
        );
    }

    /**
     * @notice Get pool stats
     * @return balance Current BNB balance
     * @return allocated Total allocated
     * @return claimed Total claimed
     * @return available Available to allocate
     */
    function getPoolStats() external view returns (
        uint256 balance,
        uint256 allocated,
        uint256 claimed,
        uint256 available
    ) {
        uint256 _balance = address(this).balance;
        uint256 _pending = totalAllocated - totalClaimedAmount;
        return (
            _balance,
            totalAllocated,
            totalClaimedAmount,
            _balance > _pending ? _balance - _pending : 0
        );
    }

    /**
     * @notice Set minimum claim amount
     * @param _minClaimAmount New minimum (in wei)
     */
    function setMinClaimAmount(uint256 _minClaimAmount) external onlyOwner {
        uint256 oldMin = minClaimAmount;
        minClaimAmount = _minClaimAmount;
        emit MinClaimAmountUpdated(oldMin, _minClaimAmount);
    }

    /**
     * @notice Emergency withdraw (owner only)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        require(amount <= address(this).balance, "Insufficient balance");
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");
        emit EmergencyWithdraw(amount, msg.sender);
    }

    /**
     * @notice Pause the contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Lookup referrer by code
     * @param code The referral code
     * @return The referrer's address (address(0) if not found)
     */
    function getReferrerByCode(bytes32 code) external view returns (address) {
        return referralCodes[code];
    }
}

src/ReferralRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SwapPilot Referral Rewards
 * @notice Manages referral rewards paid in PILOT tokens
 * 
 * Tokenomics:
 * - 5% of PILOT total supply (50,000,000 PILOT) allocated for referral rewards
 * - Referrers earn PILOT when their referrals make swaps
 * - Rewards are distributed based on swap volume
 * 
 * How it works:
 * 1. Referrer generates a referral code (off-chain, verified on-chain)
 * 2. New user signs up with referral code
 * 3. When referred user swaps, referrer earns PILOT rewards
 * 4. Rewards accrue and can be claimed at any time
 */
contract ReferralRewards is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    /// @notice PILOT token
    IERC20 public immutable pilotToken;

    /// @notice Total PILOT allocated for referrals (5% of 1B = 50M)
    uint256 public constant REFERRAL_ALLOCATION = 50_000_000 * 10**18;

    /// @notice Reward rate: PILOT tokens per $1 of swap volume
    /// Default: 0.1 PILOT per $1 swapped (adjustable by owner)
    uint256 public rewardRatePerDollar = 0.1 * 10**18;

    /// @notice Minimum swap volume in USD (scaled by 1e18) to trigger reward
    uint256 public minSwapVolumeUsd = 10 * 10**18; // $10 minimum

    /// @notice Maximum reward per swap (prevents exploitation)
    uint256 public maxRewardPerSwap = 1000 * 10**18; // 1000 PILOT max

    /// @notice Total rewards distributed
    uint256 public totalRewardsDistributed;

    /// @notice Authorized distributors (API backend)
    mapping(address => bool) public distributors;

    /// @notice Referral links: referrer => referral code hash
    mapping(address => bytes32) public referrerCodes;

    /// @notice Reverse lookup: code hash => referrer
    mapping(bytes32 => address) public codeToReferrer;

    /// @notice Track referred users: user => referrer
    mapping(address => address) public userReferrer;

    /// @notice Pending rewards per referrer
    mapping(address => uint256) public pendingRewards;

    /// @notice Total earned by referrer (historical)
    mapping(address => uint256) public totalEarned;

    /// @notice Total volume referred by referrer (in USD, scaled 1e18)
    mapping(address => uint256) public referredVolume;

    /// @notice Number of users referred
    mapping(address => uint256) public referralCount;

    // Events
    event ReferralCodeCreated(address indexed referrer, bytes32 codeHash);
    event UserReferred(address indexed user, address indexed referrer);
    event RewardAccrued(address indexed referrer, address indexed user, uint256 swapVolumeUsd, uint256 rewardAmount);
    event RewardsClaimed(address indexed referrer, uint256 amount);
    event DistributorUpdated(address indexed distributor, bool authorized);
    event RewardRateUpdated(uint256 oldRate, uint256 newRate);
    event MinSwapVolumeUpdated(uint256 oldMin, uint256 newMin);
    event MaxRewardPerSwapUpdated(uint256 oldMax, uint256 newMax);

    /// @notice Emitted on emergency withdrawal
    event EmergencyWithdraw(address indexed token, uint256 amount, address indexed to);

    modifier onlyDistributor() {
        require(distributors[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    constructor(address _pilotToken) Ownable(msg.sender) {
        require(_pilotToken != address(0), "Invalid PILOT address");
        pilotToken = IERC20(_pilotToken);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // REFERRAL CODE MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Register a referral code for a referrer
     * @param code The referral code (e.g., "ALICE123")
     */
    function registerReferralCode(string calldata code) external {
        require(bytes(code).length >= 3 && bytes(code).length <= 20, "Code must be 3-20 chars");

        bytes32 codeHash = keccak256(abi.encodePacked(code));
        require(codeToReferrer[codeHash] == address(0), "Code already taken");

        // Clear old code if exists
        bytes32 oldCode = referrerCodes[msg.sender];
        if (oldCode != bytes32(0)) {
            delete codeToReferrer[oldCode];
        }

        referrerCodes[msg.sender] = codeHash;
        codeToReferrer[codeHash] = msg.sender;

        emit ReferralCodeCreated(msg.sender, codeHash);
    }

    /**
     * @notice Link a user to a referrer using a referral code
     * @param user The user being referred
     * @param code The referral code
     */
    function linkUserToReferrer(address user, string calldata code) external onlyDistributor {
        require(userReferrer[user] == address(0), "User already has referrer");

        bytes32 codeHash = keccak256(abi.encodePacked(code));
        address referrer = codeToReferrer[codeHash];
        require(referrer != address(0), "Invalid referral code");
        require(referrer != user, "Cannot refer yourself");

        userReferrer[user] = referrer;
        referralCount[referrer]++;

        emit UserReferred(user, referrer);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // REWARD DISTRIBUTION
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Accrue referral reward for a swap
     * @param user The user who made the swap
     * @param swapVolumeUsd Swap volume in USD (scaled by 1e18)
     */
    function accrueReward(address user, uint256 swapVolumeUsd) external onlyDistributor whenNotPaused {
        address referrer = userReferrer[user];
        if (referrer == address(0)) return; // No referrer, no reward
        if (swapVolumeUsd < minSwapVolumeUsd) return; // Below minimum

        // Check allocation remaining
        uint256 remaining = getRemainingAllocation();
        if (remaining == 0) return;

        // Calculate reward
        uint256 reward = (swapVolumeUsd * rewardRatePerDollar) / 10**18;
        if (reward > maxRewardPerSwap) {
            reward = maxRewardPerSwap;
        }
        if (reward > remaining) {
            reward = remaining;
        }

        // Accrue reward
        pendingRewards[referrer] += reward;
        totalEarned[referrer] += reward;
        referredVolume[referrer] += swapVolumeUsd;
        totalRewardsDistributed += reward;

        emit RewardAccrued(referrer, user, swapVolumeUsd, reward);
    }

    /**
     * @notice Claim pending rewards
     */
    function claimRewards() external nonReentrant whenNotPaused {
        uint256 amount = pendingRewards[msg.sender];
        require(amount > 0, "No rewards to claim");

        pendingRewards[msg.sender] = 0;
        pilotToken.safeTransfer(msg.sender, amount);

        emit RewardsClaimed(msg.sender, amount);
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Get remaining PILOT allocation for referrals
     */
    function getRemainingAllocation() public view returns (uint256) {
        uint256 balance = pilotToken.balanceOf(address(this));
        uint256 remaining = REFERRAL_ALLOCATION > totalRewardsDistributed
            ? REFERRAL_ALLOCATION - totalRewardsDistributed
            : 0;
        return balance < remaining ? balance : remaining;
    }

    /**
     * @notice Get referrer stats
     */
    function getReferrerStats(address referrer) external view returns (
        uint256 pending,
        uint256 totalEarnedAmount,
        uint256 volumeReferred,
        uint256 usersReferred,
        bytes32 codeHash
    ) {
        return (
            pendingRewards[referrer],
            totalEarned[referrer],
            referredVolume[referrer],
            referralCount[referrer],
            referrerCodes[referrer]
        );
    }

    /**
     * @notice Check if a referral code is available
     */
    function isCodeAvailable(string calldata code) external view returns (bool) {
        bytes32 codeHash = keccak256(abi.encodePacked(code));
        return codeToReferrer[codeHash] == address(0);
    }

    /**
     * @notice Get referrer address from code
     */
    function getReferrerFromCode(string calldata code) external view returns (address) {
        bytes32 codeHash = keccak256(abi.encodePacked(code));
        return codeToReferrer[codeHash];
    }

    // ═══════════════════════════════════════════════════════════════════════════
    // ADMIN FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════════

    /**
     * @notice Set distributor authorization
     */
    function setDistributor(address distributor, bool authorized) external onlyOwner {
        distributors[distributor] = authorized;
        emit DistributorUpdated(distributor, authorized);
    }

    /**
     * @notice Update reward rate
     * @param newRatePerDollar New rate in PILOT per $1 (scaled 1e18)
     */
    function setRewardRate(uint256 newRatePerDollar) external onlyOwner {
        uint256 oldRate = rewardRatePerDollar;
        rewardRatePerDollar = newRatePerDollar;
        emit RewardRateUpdated(oldRate, newRatePerDollar);
    }

    /**
     * @notice Update minimum swap volume
     */
    function setMinSwapVolume(uint256 minVolumeUsd) external onlyOwner {
        uint256 oldMin = minSwapVolumeUsd;
        minSwapVolumeUsd = minVolumeUsd;
        emit MinSwapVolumeUpdated(oldMin, minVolumeUsd);
    }

    /**
     * @notice Update maximum reward per swap
     */
    function setMaxRewardPerSwap(uint256 maxReward) external onlyOwner {
        uint256 oldMax = maxRewardPerSwap;
        maxRewardPerSwap = maxReward;
        emit MaxRewardPerSwapUpdated(oldMax, maxReward);
    }

    /**
     * @notice Emergency withdraw (owner only, for migration)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(token).safeTransfer(msg.sender, amount);
        }
        emit EmergencyWithdraw(token, amount, msg.sender);
    }

    /**
     * @notice Pause the contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

